function string_to_lat_lon_points(kml, position) {
    // Transforms string coordinates to well structured array coordinates
    var string_points = kml.Document[0].Folder[position].Placemark[0].LineString[0].coordinates[0];
    string_points = string_points.substring(7);

    var points = string_points.split("\n");
    points.pop()

    points.forEach(function(part, index) {
      this[index] = points[index].split(",");
      this[index] = this[index].map(Number)
      this[index].pop()
    }, points);

    // Change lat lon position
    points.forEach(function(part, index) { 
        var lat = points[index][0]    
        var lon = points[index][1]
        points[index][0] = lon,    
        points[index][1] = lat
    }, points);
    return points;
}

