function get_area_points(area, quantity){
    var points = [];
    var x = []
    var y = []

    area.forEach(function(part, index) { 
        x.push(area[index][0])
        y.push(area[index][1])
    }, area);

    min_x = Math.min.apply(null, x);
    max_x = Math.max.apply(null, x);
    min_y = Math.min.apply(null, y);
    max_y = Math.max.apply(null, y);

    while (points.length < quantity) {
        var random_x = (Math.random() * (max_x - min_x)) + min_x
        var random_y = (Math.random() * (max_y - min_y)) + min_y

        var inside = false;
        for (var i = 0, j = area.length - 1; i < area.length; j = i++) {
            var xi = area[i][0], yi = area[i][1];
            var xj = area[j][0], yj = area[j][1];

            var intersect = ((yi > random_y) != (yj > random_y)) && (random_x < (xj - xi) * (random_y - yi) / (yj - yi) + xi);
            if (intersect) inside = !inside;
        }  
        if (inside) points.push( {"name": points.length, "lat": random_x, "lon": random_y} )
    }

    return points;
}

